
public class Loops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// for loop
		for (int i = 0; i < 5; i++) {
			System.out.println("*");
		}

		// while loop
		int i = 1;
		while (i < 10) {
			System.out.println(i);
			i++;
		}

		// do while
		int j = 1;
		do {
			System.out.println(j);
			j++;
		} while (j < 10);

	}

}
