
public class PostAndPreIncrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 5;

		// i++ -> Post Increment

		// ++i -> Pre Increment

		int j = i++; // first assigning the value then incrementing

		System.out.println(j);

		j = ++i; // first incrementing then assigning

		System.out.println(j);

	}

}
